import React from 'react'
import './index.less'
import {Card, Button, Icon, Row, Col} from 'antd'
export default class Details extends React.Component {

    handleClick = () => {
        window.location.href = '/#/admin/record';
    }

    render() {
        return (
            <div className="container">
                <Card className="card-wrap" bordered={false} bodyStyle={{padding: "24px 0"}}>
                    <Button className="returnBtn" onClick={this.handleClick}><Icon type="arrow-left"/>Medical Record / Add a new record</Button>
                </Card>
                <Card className="card-wrap" bordered={false} bodyStyle={{padding: "24px 0"}}>
                    <div style={{fontSize: '24px'}}>Attachments</div>
                </Card>
                <div>
                     <Col span={8} style={{marginLeft: '40px'}}>
                        <Card className="card-wrap-upload" style={{height: 122}}>
                            <div>Record 01</div>
                            <div>Record 02</div>
                        </Card>
                    </Col>
                    <Col span={8} style={{marginLeft: '40px'}}>
                        <Card className="card-wrap-upload"  style={{height: 122}}>  
                            <Button><Icon type="plus" /></Button>
                        </Card>
                    </Col>
                </div>
                {/* <Row gutter={16}>

                </Row> */}
            </div>
        );
    }
}
