import './index.less'
import {Card, Button, Icon, Form, Table, Modal} from 'antd'
export default class Record extends React.Component {
    render(){
        const columns = [
            {
              title: 'Date',
              dataIndex: 'Date',
              key: 'Date',
              align: 'center',
            },
            {
              title: 'Institute',
              dataIndex: 'Institute',
              key: 'Institute',
              align: 'center',
            },
            {
              title: 'Department',
              dataIndex: 'Department',
              key: 'Department',
              align: 'center',
            },
            {
                title: 'Dept_Phone',
                dataIndex: 'Dept_Phone',
                key: 'Dept_Phone',
                align: 'center',
            },
            {
                title: 'Status',
                dataIndex: 'Status',
                key: 'Status',
                align: 'center',
            },
        ];
        const dataSource = [
            {
                Date: "2020/01/20",
                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            },
            {
                Date: "2020/01/20",
                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            },
        ];
        return (
            <div class="container">
                <Card className="cardWrap" bordered={false} bodyStyle={{padding: "24px 0"}}>
                    <HeaderForm />
                </Card>
                <Card className="cardWrap" bordered={false} style={{border:"transparent"}}>
                    <Table 
                        loading={false}
                        dataSource={dataSource}
                        columns={columns}
                        aligh= {"center"}
                        rowClassName = {(record, index) => 'table-row-style'}
                    />
                </Card>
            </div>
        );
    }
}
