        this.setState({
            confirmLoading: true,
        });
        setTimeout(()=>{
            this.setState({
                visible: false,
                confirmLoading: false,
            });
        }, 1000);
    }
    handleCancel = () => {
        this.setState({
            visible: false,
        });
    };
    render(){
        return (
            <div className="header">
                <span className="header-title">Medical Record</span>
