import React from 'react'
import './index.less'
import {Card, Button, Icon, Form, Table, message, Input, Affix} from 'antd'
import PageHeader from './PageHeader'
import Highlighter from 'react-highlight-words'
export default class Record extends React.Component {
    constructor(props) {
        super(props);
        this.state= {
            dataSource: [],
            selectedRowKeys:[],
            selectedRows:[],
            searchText: '',
            searchedColumn: '',
        };
    }

    traverseDataSource = (data)=> {
        data.map((item,index)=>{
            item.key = index;
            if (item.Flag === "true") {
                item.Flag = <button name="true" style={{backgroundColor:'transparent', border: 0}}><img  style={{width: 16}} src="/assets/icon/Flag.svg"/></button>;
            }else if (item.Flag === "false") {
                item.Flag = <button name="false" style={{backgroundColor:'transparent', border: 0}}><img style={{width: 16}}src="/assets/icon/noFlag.svg"/></button>;
            }
        })
        this.setState({
            dataSource: data,
        })
    }

    getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text =>
          this.state.searchedColumn === dataIndex ? (
            <Highlighter
              highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
              searchWords={[this.state.searchText]}
              autoEscape
              textToHighlight={text.toString()}
            />
          ) : (
            text
          ),
      });

      handleSearch = (selectedKeys, confirm, dataIndex) => {
        confirm();
        this.setState({
          searchText: selectedKeys[0],
          searchedColumn: dataIndex,
        });
      };

      handleReset = clearFilters => {
        clearFilters();
        this.setState({ 
            searchText: '',
        });
      };


    componentDidMount() {
        const dataSource = [
            {  
                Flag: "true",
                Date: "04/27/2020 11:40AM",
                Department: "Cosmetic",
                Provider: "Rangar Lothbrook",
                Clinic: "Chestnut Hills Dental",
                Dept_Phone: "412-400-4707"
            },
            {
                Flag: "false",
