                Department: "Orthopaedic",
                Provider: "Rangar Lothbrook",
                Clinic: "UPMC Shadyside",
                Dept_Phone: "412-787-0101"
            }
            ,
            {
                Flag: "true",
                Date: "08/28/2019 10:50AM",
                Department: "Surgical",
                Provider: "Rangar Lothbrook",
                Clinic: "UPMC Shadyside",
                Dept_Phone: "412-787-0101"
            }
            ,
            {
                Flag: "false",
                Date: "08/28/2019 10:50AM",
                Department: "Surgical",
                Provider: "Rangar Lothbrook",
                Clinic: "UPMC Shadyside",
                Dept_Phone: "412-787-0101"
            }
            ,
            {
                Flag: "true",
                Date: "08/28/2019 10:50AM",
                Department: "Surgical",
                Provider: "Rangar Lothbrook",
                Clinic: "UPMC Shadyside",
                Dept_Phone: "412-787-0101"
            },
            {
                Flag: "true",
                Date: "08/28/2019 10:50AM",
                Department: "Surgical",
                Provider: "Rangar Lothbrook",
                Clinic: "UPMC Shadyside",
                Dept_Phone: "412-787-0101"
            },
            {
                Flag: "false",
