                Department: "Surgical",
                Provider: "Rangar Lothbrook",
                Clinic: "UPMC Shadyside",
                Dept_Phone: "412-787-0101"
            }
        ];
        this.traverseDataSource(dataSource);
    }

    render(){
        const rowSelection = {
            onChange: (selectedRowKeys, selectedRows) => {
                this.setState({
                    selectedRowKeys: selectedRowKeys,
                    selectedRows: selectedRows
                })
            },
            getCheckboxProps: record => ({
                disabled: record.name === 'Disabled User',
                name: record.name,
              }),
        };

        const columns = [
            {
              title: '',
              dataIndex: 'Flag',
              key: 'Flag',
              onCell: (record) => ({
                onClick: ()=>{
                    let data = this.state.dataSource;
                    if (record.Flag !== "true" && record.Flag !== "false") {
                        if (record.Flag.props.name === "true") {
                            record.Flag="";
                            record.Flag="false";
                        }else {
                            record.Flag="";
                            record.Flag="true";
                        }
                    }else {
                        record.Flag = "";
                        record.Flag = "true";
                    }
                    this.setState({
                        dataSource: data
                    })
                    this.traverseDataSource(data);
                }
              })
            },
            {
              title: 'Date',
              dataIndex: 'Date',
              key: 'Date',
              onCell: (record) => ({
                  onClick: ()=>{
                    window.location.href = '/#/details';
                  }
              })
            },
            {
              title: 'Department',
              dataIndex: 'Department',
              key: 'Department',
              onCell: (record) => ({
                onClick: ()=>{
                  window.location.href = '/#/details';
                }
              }),
              ...this.getColumnSearchProps('Department'),

            },
            {
                title: 'Provider',
                dataIndex: 'Provider',
                key: 'Provider',
                onCell: (record) => ({
                    onClick: ()=>{
                      window.location.href = '/#/details';
                    }
                  }),
                ...this.getColumnSearchProps('Provider')
            },
            {
                title: 'Clinic',
