import React from 'react'
import {Button, Modal, Upload, Icon, message, Card, Dropdown, Menu, Input, Avatar, Row,Col, Radio, Divider} from 'antd'
import './PageHeader.less'
const {Dragger} = Upload;

export default class HeaderForm extends React.Component{
    state = {
        recordVisible: false,
        flagVisible: false,
        filterVisible: false,
        confirmRecordLoading: false,
        confirmFlagLoading: false,
        confirmFilterLoading: false,
        sharedEMRContact: [
            {
                name: "Tarika Jain",
                email: "upmcjain@upmc.com"
            },
            {
                name: "Tarika Jain",
                email: "upmcjain@upmc.com"
            },
            {
                name: "Tarika Jain",
                email: "upmcjain@upmc.com"
            }
        ]
    };
    showFilterModal = () => {
        this.setState({
            filterVisible: true,
        });
    };
    handleFilterOk = () => {
        this.setState({
            confirmFilterLoading: true,
        });
        setTimeout(()=>{
            this.setState({
                filterVisible: false,
                confirmFilterLoading: false,
            });
            message.success("You search result have been successfully displayed.");
        }, 1000);
    }
    handleFilterCancel = () => {
        this.setState({
            filterVisible: false,
        });
    };

    showRecordModal = () => {
        this.setState({
            recordVisible: true,
        });
    };
    handleRecordOk = () => {
        this.setState({
            confirmRecordLoading: true,
        });
        setTimeout(()=>{
