import React from 'react'
import './index.less'
import {Card, Button, Icon, Form, Table, message} from 'antd'
import PageHeader from './PageHeader'
export default class Record extends React.Component {
    constructor(props) {
        super(props);
        this.state= {
            dataSource: []
        };
    }
    componentDidMount() {
        const dataSource = [
            {  
                Flag: "true",
                Date: "2020/01/20",
                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            },
            {
                Flag: "false",
                Date: "2020/01/20",
                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            },
            {
                Flag: "true",
