                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            },
            {
                Flag: "false",
                Date: "2020/01/20",
                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            },
            {
                Flag: "false",
                Date: "2020/01/20",
                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            },
            {
                Flag: "true",
                Date: "2020/01/20",
                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            }
            ,
            {
                Flag: "true",
                Date: "2020/01/20",
                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            }
            ,
            {
                Flag: "false",
                Date: "2020/01/20",
                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            }
            ,
            {
                Flag: "true",
                Date: "2020/01/20",
                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            }
            ,
            {
                Flag: "true",
                Date: "2020/01/20",
                Institute: "UMPC",
                Department: "CMU",
                Dept_Phone: "765-325-6984",
                Status: "Finished"
            }
        ];
        dataSource.map((item,index)=>{
            item.key = index;
            if (item.Flag === "true") {
                item.Flag = <span><Icon type="flag" theme="filled" /></span>;
            }else {
                item.Flag = ""
            }
        })
        this.setState({
            dataSource: dataSource
        })
    }

    render(){
        const rowSelection = {
            onChange: (selectedRowKeys, selectedRows) => {
              console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
            },
            getCheckboxProps: record => ({
              disabled: record.name === 'Disabled User', // Column configuration not to be checked
              name: record.name,
            }),
          };
        const columns = [
            {
              title: '',
              dataIndex: 'Flag',
              key: 'Flag',
            },
            {
              title: 'Date',
              dataIndex: 'Date',
              key: 'Date',
            },
            {
              title: 'Institute',
              dataIndex: 'Institute',
              key: 'Institute',
            },
            {
              title: 'Department',
              dataIndex: 'Department',
              key: 'Department',
            },
            {
                title: 'Dept_Phone',
                dataIndex: 'Dept_Phone',
                key: 'Dept_Phone',
            },
            {
                title: 'Status',
                dataIndex: 'Status',
                key: 'Status',
            },
        ];
        return (
            <div class="record-container">
                <PageHeader />
                <Card className="cardWrap" bordered={false}>
                    <Table
                        className="table-style"
                        loading={false}
                        dataSource={this.state.dataSource}
                        columns={columns}
                        align= {"left"}
                        rowSelection={rowSelection}
                        rowClassName = {(record, index) => 'table-row-style'}
                    />
                </Card>
            </div>
        );
    }
}
